import React, { useState, useEffect, useContext } from "react";
import GeneralContext from "./GeneralContext";
import { Tooltip, Grow } from "@mui/material";
import {
  BarChartOutlined,
  KeyboardArrowDown,
  KeyboardArrowUp,
  MoreHoriz,
} from "@mui/icons-material";
import { DoughnutChart } from "./DoughnoutChart";

const WatchList = () => {
  const [stocks, setStocks] = useState([]); // Dynamic stock data
  const [loading, setLoading] = useState(true);

  // Fetch real-time stock data from your backend
  const fetchStockData = async () => {
    try {
      const symbols = ["AAPL", "MSFT", "GOOGL"]; // Example: Replace with your watchlist symbols
      const promises = symbols.map((symbol) =>
        fetch(`http://localhost:3002/api/stock/${symbol}`).then((res) => res.json())
      );
      const results = await Promise.all(promises);

      // Process Alpha Vantage data
      const updatedStocks = results.map((data, index) => {
        const timeSeries = data["Time Series (5min)"];
        if (!timeSeries) return null;

        const latestTime = Object.keys(timeSeries)[0];
        const latestData = timeSeries[latestTime];
        const currentPrice = parseFloat(latestData["4. close"]);
        const prevPrice = parseFloat(Object.values(timeSeries)[1]["4. close"]); // Previous close for comparison

        return {
          name: symbols[index],
          price: currentPrice,
          percent: (((currentPrice - prevPrice) / prevPrice) * 100).toFixed(2) + "%",
          isDown: currentPrice < prevPrice,
        };
      }).filter(Boolean);

      setStocks(updatedStocks);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching stock data:", error);
      setLoading(false);
    }
  };

  // Fetch data initially and poll every 30 seconds
  useEffect(() => {
    fetchStockData();
    const interval = setInterval(fetchStockData, 30000); // Poll every 30 seconds
    return () => clearInterval(interval); // Cleanup on unmount
  }, []);

  // Prepare data for DoughnutChart
  const data = {
    labels: stocks.map((stock) => stock.name),
    datasets: [
      {
        label: "Price",
        data: stocks.map((stock) => stock.price),
        backgroundColor: [
          "rgba(255, 99, 132, 0.5)",
          "rgba(54, 162, 235, 0.5)",
          "rgba(255, 206, 86, 0.5)",
          "rgba(75, 192, 192, 0.5)",
          "rgba(153, 102, 255, 0.5)",
          "rgba(255, 159, 64, 0.5)",
        ],
        borderColor: [
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(255, 206, 86, 1)",
          "rgba(75, 192, 192, 1)",
          "rgba(153, 102, 255, 1)",
          "rgba(255, 159, 64, 1)",
        ],
        borderWidth: 1,
      },
    ],
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="watchlist-container">
      <div className="search-container">
        <input
          type="text"
          name="search"
          id="search"
          placeholder="Search eg:infy, bse, nifty fut weekly, gold mcx"
          className="search"
        />
        <span className="counts"> {stocks.length} / 50</span>
      </div>
      <p style={{ /* your existing styles */ }}>
        <small>
          <em>
            Trading is active only during <strong style={{ color: "#00c292", fontWeight: 600 }}>9:15 AM - 3:30 PM</strong> IST, Monday to Friday. Stay updated!
          </em>
        </small>
      </p>
      <ul className="list">
        {stocks.map((stock, index) => (
          <WatchListItem stock={stock} key={index} />
        ))}
      </ul>
      <DoughnutChart data={data} />
    </div>
  );
};

export default WatchList;

// WatchListItem and WatchListActions remain unchanged
const WatchListItem = ({ stock }) => {
  const [showWatchlistActions, setShowWatchlistActions] = useState(false);

  const handleMouseEnter = () => setShowWatchlistActions(true);
  const handleMouseLeave = () => setShowWatchlistActions(false);

  return (
    <li onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
      <div className="item">
        <p className={stock.isDown ? "down" : "up"}>{stock.name}</p>
        <div className="itemInfo">
          <span className="percent">{stock.percent}</span>
          {stock.isDown ? (
            <KeyboardArrowDown className="down" />
          ) : (
            <KeyboardArrowUp className="down" />
          )}
          <span className="price">{stock.price}</span>
        </div>
      </div>
      {showWatchlistActions && <WatchListActions uid={stock.name} />}
    </li>
  );
};

const WatchListActions = ({ uid }) => {
  const generalContext = useContext(GeneralContext);

  const handleBuyClick = () => {
    generalContext.openBuyWindow(uid);
  };

  return (
    <span className="actions">
      <span>
        <Tooltip title="Buy (B)" placement="top" arrow TransitionComponent={Grow}>
          <button className="buy" onClick={handleBuyClick}>Buy</button>
        </Tooltip>
        <Tooltip title="Sell (S)" placement="top" arrow TransitionComponent={Grow}>
          <button className="sell">Sell</button>
        </Tooltip>
        <Tooltip title="Analytics (A)" placement="top" arrow TransitionComponent={Grow}>
          <button className="action">
            <BarChartOutlined className="icon" />
          </button>
        </Tooltip>
        <Tooltip title="More" placement="top" arrow TransitionComponent={Grow}>
          <button className="action">
            <MoreHoriz className="icon" />
          </button>
        </Tooltip>
      </span>
    </span>
  );
};
