import React, { useState } from "react";
import { Card, CardContent } from "./ui/card"; // Adjust path as needed
import {
  FaChartLine,
  FaMobileAlt,
  FaShieldAlt,
  FaMoneyBillWave,
  FaClipboardList,
  FaRobot,
  FaLightbulb,
  FaChartBar,
} from "react-icons/fa";

const appsData = [
  {
    name: "Kite",
    description: "A sleek and powerful trading platform with advanced charting and analysis tools.",
    icon: <FaChartLine className="text-blue-600 text-5xl" />,
    bgColor: "bg-blue-100",
  },
  {
    name: "Coin",
    description: "Invest in direct mutual funds with zero commission.",
    icon: <FaMobileAlt className="text-green-600 text-5xl" />,
    bgColor: "bg-green-100",
  },
  {
    name: "Console",
    description: "The central dashboard for your Zerodha account, providing reports and insights.",
    icon: <FaShieldAlt className="text-yellow-600 text-5xl" />,
    bgColor: "bg-yellow-100",
  },
  {
    name: "Varsity",
    description: "A free and comprehensive stock market education platform by Zerodha.",
    icon: <FaClipboardList className="text-purple-600 text-5xl" />,
    bgColor: "bg-purple-100",
  },
  {
    name: "GoldenPi",
    description: "Invest in bonds and debentures directly from the Zerodha platform.",
    icon: <FaMoneyBillWave className="text-red-600 text-5xl" />,
    bgColor: "bg-red-100",
  },
  {
    name: "Streak",
    description: "Algo trading without coding. Backtest and deploy strategies easily.",
    icon: <FaRobot className="text-orange-600 text-5xl" />,
    bgColor: "bg-orange-100",
  },
  {
    name: "Sensibull",
    description: "Options trading strategies and analysis.",
    icon: <FaChartBar className="text-indigo-600 text-5xl" />,
    bgColor: "bg-indigo-100",
  },
  {
    name: "Smallcase",
    description: "Thematic investment portfolios to diversify investments.",
    icon: <FaLightbulb className="text-teal-600 text-5xl" />,
    bgColor: "bg-teal-100",
  },
];

const Apps = () => {
  const [activeFaq, setActiveFaq] = useState(null);

  return (
    <div className="bg-gray-50 py-16 px-4 sm:px-6 lg:px-8">
      {/* Header */}
      <div className="text-center mb-12">
        <h1 className="text-4xl md:text-5xl font-extrabold text-gray-800 tracking-tight">
          Zerodha Apps & Tools
        </h1>
        <p className="mt-4 text-lg text-gray-600 max-w-2xl mx-auto">
          Explore various apps and platforms offered by Zerodha to enhance your trading and investment experience.
        </p>
      </div>

      {/* Apps Grid */}
      <div className="max-w-7xl mx-auto grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {appsData.map((app, index) => (
          <Card
            key={index}
            className={`group ${app.bgColor} rounded-xl shadow-md hover:shadow-xl transition-all duration-300 transform hover:-translate-y-2`}
          >
            <CardContent className="p-6 text-center">
              <div className="flex justify-center mb-4">{app.icon}</div>
              <h2 className="text-xl font-bold text-gray-800 mb-2">{app.name}</h2>
              <p className="text-gray-600 text-sm leading-relaxed">{app.description}</p>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Trading Tips Section */}
      <div className="max-w-4xl mx-auto mt-16">
        <h2 className="text-3xl font-bold text-gray-800 mb-6 text-center">Trading Tips for Beginners</h2>
        <ul className="space-y-4">
          {[
            "Always do your research before investing in a stock.",
            "Diversify your portfolio to reduce risk.",
            "Don't invest based on emotions; follow a strategy.",
            "Understand the basics of fundamental and technical analysis.",
            "Start with small investments and gradually increase as you gain confidence.",
          ].map((tip, index) => (
            <li
              key={index}
              className="bg-white p-4 rounded-lg shadow-sm hover:bg-indigo-50 transition-colors duration-200 text-gray-700"
            >
              {tip}
            </li>
          ))}
        </ul>
      </div>

      {/* Stock Market Basics Section */}
      <div className="max-w-4xl mx-auto mt-16">
        <h2 className="text-3xl font-bold text-gray-800 mb-6 text-center">Stock Market Basics</h2>
        <ul className="space-y-4">
          {[
            "Stock Market: A marketplace where stocks are bought and sold.",
            "Stocks: Shares of a company that represent ownership.",
            "IPO (Initial Public Offering): The first sale of stock by a company to the public.",
            "Bull & Bear Market: A bull market means prices are rising, and a bear market means prices are falling.",
            "Fundamental Analysis: Evaluating a company based on its financial statements, management, and industry trends.",
            "Technical Analysis: Analyzing price charts and trading volumes to predict future stock movements.",
            "Liquidity: The ease with which a stock can be bought or sold in the market.",
            "Market Orders vs. Limit Orders: Market orders execute at the current price, while limit orders execute at a set price.",
          ].map((basic, index) => (
            <li
              key={index}
              className="bg-white p-4 rounded-lg shadow-sm hover:bg-indigo-50 transition-colors duration-200 text-gray-700"
            >
              {basic}
            </li>
          ))}
        </ul>
      </div>

      {/* FAQ Section */}
      <div className="max-w-4xl mx-auto mt-16">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-8">FAQ</h2>
        <div className="faq-container bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
          {[
            { question: "What is Zerodha?", answer: "Zerodha is India's largest stock brokerage firm, offering low-cost trading solutions." },
            { question: "How do I open an account?", answer: "You can open an account online by submitting your KYC documents and completing verification." },
            { question: "What are the charges for trading?", answer: "Zerodha offers zero brokerage for equity investments and Rs. 20 per executed order for intraday and F&O trades." },
            { question: "Is my money safe with Zerodha?", answer: "Yes, Zerodha is a registered broker with SEBI and follows all regulatory compliance norms." },
          ].map((faq, index) => (
            <div
              key={index}
              className={`faq-item p-5 border-b border-gray-200 last:border-b-0 cursor-pointer transition-all duration-300 ${
                activeFaq === index ? 'bg-indigo-50 shadow-md' : 'hover:bg-gray-50'
              }`}
              onClick={() => setActiveFaq(activeFaq === index ? null : index)}
            >
              <div className="flex justify-between items-center">
                <h3
                  className={`faq-question text-lg font-semibold ${
                    activeFaq === index ? 'text-indigo-600' : 'text-gray-700'
                  } transition-colors duration-300`}
                >
                  {faq.question}
                </h3>
                <span
                  className={`arrow text-xl font-bold text-indigo-500 transition-transform duration-300 ${
                    activeFaq === index ? 'rotate-180' : ''
                  }`}
                >
                  {activeFaq === index ? '▲' : '▼'}
                </span>
              </div>
              {activeFaq === index && (
                <p className="faq-answer mt-3 text-gray-600 text-base leading-relaxed animate-fade-in">
                  {faq.answer}
                </p>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Apps;
