import React, { useState, useEffect, useContext } from "react";
import GeneralContext from "./GeneralContext";
import { Tooltip, Grow } from "@mui/material";
import {
  BarChartOutlined,
  KeyboardArrowDown,
  KeyboardArrowUp,
  MoreHoriz,
} from "@mui/icons-material";
import { DoughnutChart } from "./DoughnoutChart";

// Sample static watchlist data (you can adjust these values)
const initialWatchlist = [
  { name: "INFY", price: 20.08, percent: "0.00%", isDown: false },
  { name: "MSFT", price: 400.50, percent: "0.00%", isDown: false },
  { name: "AAPL", price: 180.25, percent: "0.00%", isDown: false },
  { name: "GOOGL", price: 140.75, percent: "0.00%", isDown: false },
  { name: "TSLA", price: 250.30, percent: "0.00%", isDown: false },
];

const WatchList = () => {
  const [stocks, setStocks] = useState(initialWatchlist);
  const [error, setError] = useState(null);

  // Function to simulate random price changes every 3 seconds
  const updateStockPrices = () => {
    setStocks((prevStocks) =>
      prevStocks.map((stock) => {
        // Generate a random percentage change between -2% and +2%
        const maxChange = 0.02; // 2% limit
        const minChange = -0.02; // -2% limit
        const randomChange = Math.random() * (maxChange - minChange) + minChange;

        // Calculate new price
        const newPrice = Math.max(0, stock.price * (1 + randomChange)).toFixed(2);

        // Calculate percentage change
        const priceChange = (newPrice - stock.price) / stock.price * 100;
        const percentChange = priceChange.toFixed(2) + "%";

        // Determine if price is down
        const isDown = priceChange < 0;

        return {
          ...stock,
          price: parseFloat(newPrice),
          percent: percentChange,
          isDown,
        };
      })
    );
  };

  // Use useEffect to update prices every 3 seconds
  useEffect(() => {
    const interval = setInterval(updateStockPrices, 3000); // Update every 3 seconds
    return () => clearInterval(interval); // Cleanup on unmount
  }, []);

  const data = {
    labels: stocks.map((stock) => stock.name),
    datasets: [
      {
        label: "Price",
        data: stocks.map((stock) => stock.price),
        backgroundColor: [
          "rgba(255, 99, 132, 0.5)",
          "rgba(54, 162, 235, 0.5)",
          "rgba(255, 206, 86, 0.5)",
          "rgba(75, 192, 192, 0.5)",
          "rgba(153, 102, 255, 0.5)",
          "rgba(255, 159, 64, 0.5)",
        ],
        borderColor: [
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(255, 206, 86, 1)",
          "rgba(75, 192, 192, 1)",
          "rgba(153, 102, 255, 1)",
          "rgba(255, 159, 64, 1)",
        ],
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="watchlist-container">
      <div className="search-container">
        <input
          type="text"
          name="search"
          id="search"
          placeholder="Search eg:infy, bse, nifty fut weekly, gold mcx"
          className="search"
        />
        <span className="counts"> {stocks.length} / 50</span>
      </div>
      <p style={{
        textAlign: "center",
        margin: "15px 0",
        fontFamily: "Arial, sans-serif",
        color: "#666",
        fontSize: "14px",
        fontStyle: "italic",
        background: "linear-gradient(to right, #f8f9fa, #e9ecef)",
        padding: "8px",
        borderRadius: "4px",
        border: "1px solid #dee2e6",
        transition: "background 0.3s ease, transform 0.3s ease, color 0.3s ease"
      }}>
        <small>
          <em>
            Trading is active only during <strong style={{ color: "#00c292", fontWeight: 600 }}>9:15 AM - 3:30 PM</strong> IST, Monday to Friday. Stay updated!
          </em>
        </small>
      </p>
      <ul className="stock-list">
        {stocks.map((stock, index) => (
          <WatchListItem stock={stock} key={index} />
        ))}
      </ul>
      <DoughnutChart data={data} />
    </div>
  );
};

export default WatchList;

const WatchListItem = ({ stock }) => {
  const [showWatchlistActions, setShowWatchlistActions] = useState(false);

  const handleMouseEnter = () => setShowWatchlistActions(true);
  const handleMouseLeave = () => setShowWatchlistActions(false);

  const handleBuyClick = () => {
    console.log(`Buy clicked for ${stock.name}`);
  };

  const handleSellClick = () => {
    console.log(`Sell clicked for ${stock.name}`);
  };

  return (
    <li
      className="stock-item"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div className="stock-info">
        <p className="stock-name">{stock.name}</p>
        <div className="stock-details">
          <span className={`percent ${stock.isDown ? "down" : "up"}`}>
            {stock.percent}
            {stock.isDown ? (
              <KeyboardArrowDown className="arrow down" />
            ) : (
              <KeyboardArrowUp className="arrow up" />
            )}
          </span>
          <span className="price">{stock.price}</span>
        </div>
      </div>
      {showWatchlistActions && (
        <div className="actions">
          <Tooltip title="Buy (B)" placement="top" arrow TransitionComponent={Grow}>
            <button className="buy" onClick={handleBuyClick}>Buy</button>
          </Tooltip>
          <Tooltip title="Sell (S)" placement="top" arrow TransitionComponent={Grow}>
            <button className="sell" onClick={handleSellClick}>Sell</button>
          </Tooltip>
          <Tooltip title="Analytics (A)" placement="top" arrow TransitionComponent={Grow}>
            <button className="action">
              <BarChartOutlined className="icon" />
            </button>
          </Tooltip>
          <Tooltip title="More" placement="top" arrow TransitionComponent={Grow}>
            <button className="action">
              <MoreHoriz className="icon" />
            </button>
          </Tooltip>
        </div>
      )}
    </li>
  );
};

const WatchListActions = ({ uid }) => {
  const generalContext = useContext(GeneralContext);

  const handleBuyClick = () => {
    generalContext.openBuyWindow(uid);
  };

  return (
    <span className="actions">
      <span>
        <Tooltip title="Buy (B)" placement="top" arrow TransitionComponent={Grow}>
          <button className="buy" onClick={handleBuyClick}>Buy</button>
        </Tooltip>
        <Tooltip title="Sell (S)" placement="top" arrow TransitionComponent={Grow}>
          <button className="sell">Sell</button>
        </Tooltip>
        <Tooltip title="Analytics (A)" placement="top" arrow TransitionComponent={Grow}>
          <button className="action">
            <BarChartOutlined className="icon" />
          </button>
        </Tooltip>
        <Tooltip title="More" placement="top" arrow TransitionComponent={Grow}>
          <button className="action">
            <MoreHoriz className="icon" />
          </button>
        </Tooltip>
      </span>
    </span>
  );
};
