import React, { useState, useEffect, useContext } from "react";
import GeneralContext from "./GeneralContext";
import { Tooltip, Grow } from "@mui/material";
import { BarChartOutlined, KeyboardArrowDown, KeyboardArrowUp, MoreHoriz } from "@mui/icons-material";
import { DoughnutChart } from "./DoughnoutChart";

// Initial static stock data
const initialWatchlist = [
  { name: "INFY", price: 1600, percent: "0.00%", isDown: false },
  { name: "TCS", price: 3500, percent: "0.00%", isDown: false },
  { name: "RELIANCE", price: 2600, percent: "0.00%", isDown: false },
  { name: "HDFC", price: 2800, percent: "0.00%", isDown: false },
  { name: "SBIN", price: 600, percent: "0.00%", isDown: false },
];

const WatchList = () => {
  const [watchlist, setWatchlist] = useState(initialWatchlist);

  useEffect(() => {
    const interval = setInterval(() => {
      setWatchlist((prevStocks) =>
        prevStocks.map((stock) => {
          const change = (Math.random() * 2 - 1) * 10; // Random change between -10 and +10
          const newPrice = Math.max(10, stock.price + change); // Ensuring price stays positive
          const isDown = change < 0;
          const percentChange = ((change / stock.price) * 100).toFixed(2) + "%";

          return { ...stock, price: newPrice.toFixed(2), percent: percentChange, isDown };
        })
      );
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  const data = {
    labels: watchlist.map((stock) => stock.name),
    datasets: [
      {
        label: "Price",
        data: watchlist.map((stock) => stock.price),
        backgroundColor: [
          "rgba(255, 99, 132, 0.5)",
          "rgba(54, 162, 235, 0.5)",
          "rgba(255, 206, 86, 0.5)",
          "rgba(75, 192, 192, 0.5)",
          "rgba(153, 102, 255, 0.5)",
        ],
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="watchlist-container">
      <div className="search-container">
        <input type="text" placeholder="Search stocks..." className="search" />
        <span className="counts"> {watchlist.length} / 50</span>
      </div>
      <p style={{ textAlign: "center", margin: "15px 0", fontStyle: "italic" }}>
        <small>
          <em>Trading is active only during <strong>9:15 AM - 3:30 PM</strong> IST.</em>
        </small>
      </p>
      <ul className="list">
        {watchlist.map((stock, index) => (
          <WatchListItem stock={stock} key={index} />
        ))}
      </ul>
      <DoughnutChart data={data} />
    </div>
  );
};

const WatchListItem = ({ stock }) => {
  const [showActions, setShowActions] = useState(false);

  return (
    <li onMouseEnter={() => setShowActions(true)} onMouseLeave={() => setShowActions(false)}>
      <div className="item">
        <p className={stock.isDown ? "down" : "up"}>{stock.name}</p>
        <div className="itemInfo">
          <span className="percent">{stock.percent}</span>
          {stock.isDown ? <KeyboardArrowDown className="down" /> : <KeyboardArrowUp className="up" />}
          <span className="price">â‚¹{stock.price}</span>
        </div>
      </div>
      {showActions && <WatchListActions uid={stock.name} />}
    </li>
  );
};

const WatchListActions = ({ uid }) => {
  const generalContext = useContext(GeneralContext);
  return (
    <span className="actions">
      <Tooltip title="Buy" placement="top" arrow TransitionComponent={Grow}>
        <button className="buy" onClick={() => generalContext.openBuyWindow(uid)}>Buy</button>
      </Tooltip>
      <Tooltip title="Sell" placement="top" arrow TransitionComponent={Grow}>
        <button className="sell">Sell</button>
      </Tooltip>
      <Tooltip title="Analytics" placement="top" arrow TransitionComponent={Grow}>
        <button className="action"><BarChartOutlined className="icon" /></button>
      </Tooltip>
      <Tooltip title="More" placement="top" arrow TransitionComponent={Grow}>
        <button className="action"><MoreHoriz className="icon" /></button>
      </Tooltip>
    </span>
  );
};

export default WatchList;
