import React from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar } from "react-chartjs-2";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const options = {
  responsive: true,
  maintainAspectRatio: false, // Ensures better responsiveness
  plugins: {
    legend: {
      display: true,
      position: "top",
    },
    title: {
      display: true,
      text: "Holdings",
      font: {
        size: 18,
      },
    },
  },
  scales: {
    x: {
      grid: {
        display: false, // Hides grid lines on X-axis for cleaner look
      },
    },
    y: {
      beginAtZero: true,
      grid: {
        color: "rgba(200, 200, 200, 0.3)", // Light grid lines
      },
      ticks: {
        callback: function (value) {
          return "₹" + value; // Adds ₹ symbol to values
        },
      },
    },
  },
};

const VerticalGraph = ({ data }) => {
  return (
    <div style={{ width: "100%", height: "400px", padding: "10px" }}>
      <Bar options={options} data={data} />
    </div>
  );
};

export default VerticalGraph;
