import React, { useState, useEffect, useContext } from "react";
import GeneralContext from "./GeneralContext";
import { Tooltip, Grow } from "@mui/material";
import {
  BarChartOutlined,
  KeyboardArrowDown,
  KeyboardArrowUp,
  MoreHoriz,
} from "@mui/icons-material";
import { DoughnutChart } from "./DoughnoutChart";

const WatchList = () => {
  const [stocks, setStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchStockData = async () => {
    try {
      const symbols = ["INFY", "MSFT", "AAPL", "GOOGL", "TSLA"]; // Use supported symbols (INFY is Infosys ADR)
      const promises = symbols.map((symbol) =>
        fetch(`http://localhost:3002/api/stock/${symbol}`).then((res) => res.json())
      );
      const results = await Promise.all(promises);

      console.log("API Response:", results);

      const updatedStocks = results.map((data, index) => {
        console.log(`Processing symbol ${symbols[index]}:`, data);

        // Check if the response has the expected structure
        if (!data || !data["Time Series (5min)"]) {
          console.warn(`No data for symbol ${symbols[index]}`, data);
          return null; // Skip invalid data
        }

        const timeSeries = data["Time Series (5min)"];
        const timestamps = Object.keys(timeSeries);
        if (timestamps.length === 0) return null;

        // Get the latest and previous data points
        const latestTime = timestamps[0];
        const prevTime = timestamps[1] || latestTime; // Use latest if no previous available
        const latestData = timeSeries[latestTime];
        const prevData = timeSeries[prevTime];

        const currentPrice = parseFloat(latestData["4. close"]) || 0;
        const prevPrice = parseFloat(prevData["4. close"]) || currentPrice;

        // Calculate percentage change
        const percentChange = ((currentPrice - prevPrice) / prevPrice * 100).toFixed(2) + "%";

        return {
          name: symbols[index],
          price: currentPrice,
          percent: percentChange,
          isDown: currentPrice < prevPrice,
        };
      }).filter(Boolean); // Remove null entries

      console.log("Processed Stocks:", updatedStocks);

      setStocks(updatedStocks);
      setLoading(false);
      setError(null);
    } catch (error) {
      console.error("Error fetching stock data:", error);
      setError("Failed to fetch stock data. Please check your API key or network.");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStockData();
    const interval = setInterval(fetchStockData, 120000); // Poll every 2 minutes to avoid rate limits
    return () => clearInterval(interval);
  }, []);

  const data = {
    labels: stocks.map((stock) => stock.name),
    datasets: [
      {
        label: "Price",
        data: stocks.map((stock) => stock.price),
        backgroundColor: [
          "rgba(255, 99, 132, 0.5)",
          "rgba(54, 162, 235, 0.5)",
          "rgba(255, 206, 86, 0.5)",
          "rgba(75, 192, 192, 0.5)",
          "rgba(153, 102, 255, 0.5)",
        ],
        borderColor: [
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(255, 206, 86, 1)",
          "rgba(75, 192, 192, 1)",
          "rgba(153, 102, 255, 1)",
        ],
        borderWidth: 1,
      },
    ],
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div style={{ color: "red" }}>{error}</div>;
  if (!stocks.length) return <div>No stock data available. Please try again later or check your API key and symbols.</div>;

  return (
    <div className="watchlist-container">
      <div className="search-container">
        <input
          type="text"
          name="search"
          id="search"
          placeholder="Search eg:infy, bse, nifty fut weekly, gold mcx"
          className="search"
        />
        <span className="counts"> {stocks.length} / 50</span>
      </div>
      <p style={{
        textAlign: "center",
        margin: "15px 0",
        fontFamily: "Arial, sans-serif",
        color: "#666",
        fontSize: "14px",
        fontStyle: "italic",
        background: "linear-gradient(to right, #f8f9fa, #e9ecef)",
        padding: "8px",
        borderRadius: "4px",
        border: "1px solid #dee2e6",
        transition: "background 0.3s ease, transform 0.3s ease, color 0.3s ease"
      }}>
        <small>
          <em>
            Trading is active only during <strong style={{ color: "#00c292", fontWeight: 600 }}>9:15 AM - 3:30 PM</strong> IST, Monday to Friday. Stay updated!
          </em>
        </small>
      </p>
      <ul className="stock-list">
        {stocks.map((stock, index) => (
          <WatchListItem stock={stock} key={index} />
        ))}
      </ul>
      <DoughnutChart data={data} />
    </div>
  );
};

export default WatchList;

// Keep WatchListItem and WatchListActions as they are from your previous code
const WatchListItem = ({ stock }) => {
  const [showWatchlistActions, setShowWatchlistActions] = useState(false);

  const handleMouseEnter = () => setShowWatchlistActions(true);
  const handleMouseLeave = () => setShowWatchlistActions(false);

  const handleBuyClick = () => {
    console.log(`Buy clicked for ${stock.name}`);
  };

  const handleSellClick = () => {
    console.log(`Sell clicked for ${stock.name}`);
  };

  return (
    <li
      className="stock-item"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div className="stock-info">
        <p className="stock-name">{stock.name}</p>
        <div className="stock-details">
          <span className={`percent ${stock.isDown ? "down" : "up"}`}>
            {stock.percent}
            {stock.isDown ? (
              <KeyboardArrowDown className="arrow down" />
            ) : (
              <KeyboardArrowUp className="arrow up" />
            )}
          </span>
          <span className="price">{stock.price.toFixed(2)}</span>
        </div>
      </div>
      {showWatchlistActions && (
        <div className="actions">
          <Tooltip title="Buy (B)" placement="top" arrow TransitionComponent={Grow}>
            <button className="buy" onClick={handleBuyClick}>Buy</button>
          </Tooltip>
          <Tooltip title="Sell (S)" placement="top" arrow TransitionComponent={Grow}>
            <button className="sell" onClick={handleSellClick}>Sell</button>
          </Tooltip>
          <Tooltip title="Analytics (A)" placement="top" arrow TransitionComponent={Grow}>
            <button className="action">
              <BarChartOutlined className="icon" />
            </button>
          </Tooltip>
          <Tooltip title="More" placement="top" arrow TransitionComponent={Grow}>
            <button className="action">
              <MoreHoriz className="icon" />
            </button>
          </Tooltip>
        </div>
      )}
    </li>
  );
};

const WatchListActions = ({ uid }) => {
  const generalContext = useContext(GeneralContext);

  const handleBuyClick = () => {
    generalContext.openBuyWindow(uid);
  };

  return (
    <span className="actions">
      <span>
        <Tooltip title="Buy (B)" placement="top" arrow TransitionComponent={Grow}>
          <button className="buy" onClick={handleBuyClick}>Buy</button>
        </Tooltip>
        <Tooltip title="Sell (S)" placement="top" arrow TransitionComponent={Grow}>
          <button className="sell">Sell</button>
        </Tooltip>
        <Tooltip title="Analytics (A)" placement="top" arrow TransitionComponent={Grow}>
          <button className="action">
            <BarChartOutlined className="icon" />
          </button>
        </Tooltip>
        <Tooltip title="More" placement="top" arrow TransitionComponent={Grow}>
          <button className="action">
            <MoreHoriz className="icon" />
          </button>
        </Tooltip>
      </span>
    </span>
  );
};
