import React, { useState } from "react";
import { Card, CardContent } from "./ui/card"; // Adjust path as needed
import {
  FaChartLine,
  FaMobileAlt,
  FaShieldAlt,
  FaMoneyBillWave,
  FaClipboardList,
  FaRobot,
  FaLightbulb,
  FaChartBar,
} from "react-icons/fa";

const appsData = [
  {
    name: "Kite",
    description: "A sleek and powerful trading platform with advanced charting and analysis tools.",
    icon: <FaChartLine className="text-blue-500 text-5xl" />,
    bgColor: "bg-blue-200",
  },
  {
    name: "Coin",
    description: "Invest in direct mutual funds with zero commission.",
    icon: <FaMobileAlt className="text-green-500 text-5xl" />,
    bgColor: "bg-green-200",
  },
  {
    name: "Console",
    description: "The central dashboard for your Zerodha account, providing reports and insights.",
    icon: <FaShieldAlt className="text-yellow-500 text-5xl" />,
    bgColor: "bg-yellow-200",
  },
  {
    name: "Varsity",
    description: "A free and comprehensive stock market education platform by Zerodha.",
    icon: <FaClipboardList className="text-purple-500 text-5xl" />,
    bgColor: "bg-purple-200",
  },
  {
    name: "GoldenPi",
    description: "Invest in bonds and debentures directly from the Zerodha platform.",
    icon: <FaMoneyBillWave className="text-red-500 text-5xl" />,
    bgColor: "bg-red-200",
  },
  {
    name: "Streak",
    description: "Algo trading without coding. Backtest and deploy strategies easily.",
    icon: <FaRobot className="text-orange-500 text-5xl" />,
    bgColor: "bg-orange-200",
  },
  {
    name: "Sensibull",
    description: "Options trading strategies and analysis.",
    icon: <FaChartBar className="text-indigo-500 text-5xl" />,
    bgColor: "bg-indigo-200",
  },
  {
    name: "Smallcase",
    description: "Thematic investment portfolios to diversify investments.",
    icon: <FaLightbulb className="text-teal-500 text-5xl" />,
    bgColor: "bg-teal-200",
  },
];

const Apps = () => {
  const [activeFaq, setActiveFaq] = useState(null);

  return (
    <div className="app-container p-10 bg-gray-50 min-h-screen">
      <h1 className="text-4xl font-extrabold text-center text-gray-800 mb-6">Zerodha Apps & Tools</h1>
      <p className="text-lg text-center text-gray-600 mb-10">
        Explore various apps and platforms offered by Zerodha to enhance your trading and investment experience.
      </p>

      {/* Apps Section */}
      <div className="apps-grid grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 mb-16">
        {appsData.map((app, index) => (
          <Card key={index} className={`app-card rounded-lg shadow-lg ${app.bgColor} transition transform hover:scale-105`}>
            <CardContent className="app-content p-6 text-center">
              <div className="icon-container mb-4">{app.icon}</div>
              <h2 className="app-name text-2xl font-semibold text-gray-800 mb-2">{app.name}</h2>
              <p className="app-description text-sm text-gray-600">{app.description}</p>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Trading Tips Section */}
      <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">Trading Tips for Beginners</h2>
      <ul className="tips-list space-y-4 text-lg text-gray-600 max-w-2xl mx-auto">
        {[
          "Always do your research before investing in a stock.",
          "Diversify your portfolio to reduce risk.",
          "Don't invest based on emotions; follow a strategy.",
          "Understand the basics of fundamental and technical analysis.",
          "Start with small investments and gradually increase as you gain confidence.",
        ].map((tip, index) => (
          <li key={index} className="tip-item flex items-center space-x-3">
            <span className="text-xl text-green-500">✔</span>
            <span>{tip}</span>
          </li>
        ))}
      </ul>

      {/* Stock Market Basics Section */}
      <h2 className="text-3xl font-bold text-center text-gray-800 mb-6 mt-12">Stock Market Basics</h2>
      <ul className="tips-list space-y-4 text-lg text-gray-600 max-w-2xl mx-auto">
        {[
          "Stock Market: A marketplace where stocks are bought and sold.",
          "Stocks: Shares of a company that represent ownership.",
          "IPO (Initial Public Offering): The first sale of stock by a company to the public.",
          "Bull & Bear Market: A bull market means prices are rising, and a bear market means prices are falling.",
          "Fundamental Analysis: Evaluating a company based on its financial statements, management, and industry trends.",
          "Technical Analysis: Analyzing price charts and trading volumes to predict future stock movements.",
          "Liquidity: The ease with which a stock can be bought or sold in the market.",
          "Market Orders vs. Limit Orders: Market orders execute at the current price, while limit orders execute at a set price.",
        ].map((basic, index) => (
          <li key={index} className="tip-item flex items-center space-x-3">
            <span className="text-xl text-blue-500">✔</span>
            <span>{basic}</span>
          </li>
        ))}
      </ul>

      {/* FAQ Section */}
<h2 className="text-3xl font-bold text-center text-gray-800 mt-12 mb-6">Frequently Asked Questions</h2>
<div className="faq-container max-w-3xl mx-auto bg-white rounded-xl shadow-md p-6">
  {[  
    {
      question: "What trading platforms does Zerodha offer?",
      answer: "Zerodha offers Kite for trading stocks, Streak for algo trading, Sensibull for options strategies, and more. Check the Apps section above!"
    },
    {
      question: "How do I start investing with Zerodha?",
      answer: "Sign up online with your KYC details (Aadhaar, PAN, etc.), verify your account, and start using Kite or Coin to invest."
    },
    {
      question: "Are there any hidden fees?",
      answer: "No hidden fees! Equity delivery is free, and intraday/F&O trades cost ₹20 per order. Other charges (e.g., DP, SEBI fees) are transparently listed on Zerodha’s site."
    },
    {
      question: "Can beginners use Zerodha effectively?",
      answer: "Absolutely! Varsity provides free education, and the platforms are user-friendly. Start small and explore the tools."
    },
  ].map((faq, index) => (
    <div
      key={index}
      className={`faq-item p-5 border-b border-gray-200 last:border-b-0 cursor-pointer transition-all duration-300 rounded-lg ${
        activeFaq === index ? 'bg-indigo-50 shadow-inner' : 'hover:bg-gray-100'
      }`}
      onClick={() => setActiveFaq(activeFaq === index ? null : index)}
    >
      <div className="flex justify-between items-center">
        <h3
          className={`faq-question text-lg font-semibold ${
            activeFaq === index ? 'text-indigo-600' : 'text-gray-800'
          }`}
        >
          {faq.question}
        </h3>
        <span
          className={`arrow text-xl font-bold text-indigo-500 transition-transform duration-300 ${
            activeFaq === index ? 'rotate-180' : ''
          }`}
        >
          {activeFaq === index ? '▲' : '▼'}
        </span>
      </div>

      {/* FAQ Answer Section */}
      <div
        className={`faq-answer mt-3 text-gray-600 text-base leading-relaxed max-h-0 overflow-hidden transition-all duration-300 ease-in-out ${
          activeFaq === index ? 'max-h-[1000px] p-3' : '' // Adjust max-height for smooth expansion
        }`}
      >
        {faq.answer}
      </div>
    </div>
  ))}
</div>

    </div>
  );
};

export default Apps;
