import React, { useState } from "react";
import { Card, CardContent, Typography } from "@mui/material"; // Import MUI Card
import { FaChartLine, FaMobileAlt, FaShieldAlt, FaMoneyBillWave, FaClipboardList, FaRobot, FaLightbulb, FaChartBar } from "react-icons/fa";

const appsData = [
  {
    name: "Kite",
    description: "A sleek and powerful trading platform with advanced charting and analysis tools.",
    icon: <FaChartLine className="text-blue-500 text-3xl" />,
  },
  {
    name: "Coin",
    description: "Invest in direct mutual funds with zero commission.",
    icon: <FaMobileAlt className="text-green-500 text-3xl" />,
  },
  {
    name: "Console",
    description: "The central dashboard for your Zerodha account, providing reports and insights.",
    icon: <FaShieldAlt className="text-yellow-500 text-3xl" />,
  },
  {
    name: "Varsity",
    description: "A free and comprehensive stock market education platform by Zerodha.",
    icon: <FaClipboardList className="text-purple-500 text-3xl" />,
  },
  {
    name: "GoldenPi",
    description: "Invest in bonds and debentures directly from the Zerodha platform.",
    icon: <FaMoneyBillWave className="text-red-500 text-3xl" />,
  },
  {
    name: "Streak",
    description: "Algo trading without coding. Backtest and deploy strategies easily.",
    icon: <FaRobot className="text-orange-500 text-3xl" />,
  },
  {
    name: "Sensibull",
    description: "Options trading strategies and analysis.",
    icon: <FaChartBar className="text-indigo-500 text-3xl" />,
  },
  {
    name: "Smallcase",
    description: "Thematic investment portfolios to diversify investments.",
    icon: <FaLightbulb className="text-teal-500 text-3xl" />,
  }
];

const Apps = () => {
  return (
    <div className="p-6">
      <h1 className="text-4xl font-bold text-center mb-6">Zerodha Apps & Tools</h1>
      <p className="text-center text-gray-600 mb-4">
        Explore various apps and platforms offered by Zerodha to enhance your trading and investment experience.
      </p>

      <div className="grid md:grid-cols-3 gap-6">
        {appsData.map((app, index) => (
          <Card key={index} sx={{ padding: 2, boxShadow: 3, borderRadius: 2, transition: "0.3s", "&:hover": { boxShadow: 6 } }}>
            <CardContent className="flex flex-col items-center text-center">
              {app.icon}
              <Typography variant="h6" className="mt-4">{app.name}</Typography>
              <Typography variant="body2" color="textSecondary">{app.description}</Typography>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default Apps;
