import React, { useState } from "react";
import { Card, CardContent } from "./ui/card";
import { FaChartLine, FaMobileAlt, FaShieldAlt, FaMoneyBillWave, FaClipboardList, FaRobot, FaLightbulb, FaChartBar } from "react-icons/fa";

const appsData = [
  { name: "Kite", description: "A sleek and powerful trading platform with advanced charting and analysis tools.", icon: <FaChartLine className="text-blue-600 text-5xl" /> },
  { name: "Coin", description: "Invest in direct mutual funds with zero commission.", icon: <FaMobileAlt className="text-green-600 text-5xl" /> },
  { name: "Console", description: "The central dashboard for your Zerodha account, providing reports and insights.", icon: <FaShieldAlt className="text-yellow-600 text-5xl" /> },
  { name: "Varsity", description: "A free and comprehensive stock market education platform by Zerodha.", icon: <FaClipboardList className="text-purple-600 text-5xl" /> },
  { name: "GoldenPi", description: "Invest in bonds and debentures directly from the Zerodha platform.", icon: <FaMoneyBillWave className="text-red-600 text-5xl" /> },
  { name: "Streak", description: "Algo trading without coding. Backtest and deploy strategies easily.", icon: <FaRobot className="text-orange-600 text-5xl" /> },
  { name: "Sensibull", description: "Options trading strategies and analysis.", icon: <FaChartBar className="text-indigo-600 text-5xl" /> },
  { name: "Smallcase", description: "Thematic investment portfolios to diversify investments.", icon: <FaLightbulb className="text-teal-600 text-5xl" /> }
];

const faqs = [
  { question: "What is Zerodha?", answer: "Zerodha is India's largest stock brokerage firm, offering low-cost trading solutions." },
  { question: "How do I open an account?", answer: "You can open an account online by submitting your KYC documents and completing verification." },
  { question: "What are the charges for trading?", answer: "Zerodha offers zero brokerage for equity investments and Rs. 20 per executed order for intraday and F&O trades." },
  { question: "Is my money safe with Zerodha?", answer: "Yes, Zerodha is a registered broker with SEBI and follows all regulatory compliance norms." }
];

const Apps = () => {
  const [activeFaq, setActiveFaq] = useState(null);

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 mt-6">
  {appsData.map((app, index) => (
    <Card key={index} className="p-6 bg-white rounded-2xl shadow-md hover:shadow-xl transition transform hover:-translate-y-1">
      <CardContent className="flex flex-col items-center text-center">
        <div className="text-4xl mb-3">{app.icon}</div>
        <h2 className="text-xl font-semibold">{app.name}</h2>
        <p className="text-gray-600 mt-2">{app.description}</p>
      </CardContent>
    </Card>
  ))}
</div>

  );
};

export default Apps;
