// import React, { useState } from "react";
import { Card, CardContent } from "./ui/card";  
import { FaChartLine, FaMobileAlt, FaShieldAlt, FaMoneyBillWave, FaClipboardList, FaRobot, FaLightbulb, FaChartBar } from "react-icons/fa";

const appsData = [
  { name: "Kite", description: "A sleek and powerful trading platform with advanced charting and analysis tools.", icon: <FaChartLine className="text-blue-500 text-3xl" /> },
  { name: "Coin", description: "Invest in direct mutual funds with zero commission.", icon: <FaMobileAlt className="text-green-500 text-3xl" /> },
  { name: "Console", description: "The central dashboard for your Zerodha account, providing reports and insights.", icon: <FaShieldAlt className="text-yellow-500 text-3xl" /> },
  { name: "Varsity", description: "A free and comprehensive stock market education platform by Zerodha.", icon: <FaClipboardList className="text-purple-500 text-3xl" /> },
  { name: "GoldenPi", description: "Invest in bonds and debentures directly from the Zerodha platform.", icon: <FaMoneyBillWave className="text-red-500 text-3xl" /> },
  { name: "Streak", description: "Algo trading without coding. Backtest and deploy strategies easily.", icon: <FaRobot className="text-orange-500 text-3xl" /> },
  { name: "Sensibull", description: "Options trading strategies and analysis.", icon: <FaChartBar className="text-indigo-500 text-3xl" /> },
  { name: "Smallcase", description: "Thematic investment portfolios to diversify investments.", icon: <FaLightbulb className="text-teal-500 text-3xl" /> }
];

const Apps = () => {
  return (
    <div className="p-6">
      <h1 className="text-4xl font-bold text-center mb-6">Zerodha Apps & Tools</h1>
      <p className="text-center text-gray-600 mb-6">Explore various apps and platforms offered by Zerodha to enhance your trading and investment experience.</p>

      {/* Grid Layout for Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {appsData.map((app, index) => (
          <Card key={index} className="p-6 shadow-lg rounded-lg bg-white border hover:shadow-xl transition">
            <CardContent className="flex items-center space-x-4">
              {app.icon}
              <div>
                <h2 className="text-xl font-semibold">{app.name}</h2>
                <p className="text-gray-600">{app.description}</p>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default Apps;
